function predictImage() {
    console.log('processing...');
    let image = cv.imread(canvas);
    cv.cvtColor(image, image, cv.COLOR_RGBA2GRAY, 0);
    cv.threshold(image, image, 175, 255, cv.THRESH_BINARY);

    let contours = new cv.MatVector();
    let hierarchy = new cv.Mat();
    cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);

    let cnt = contours.get(0);
    let rect = cv.boundingRect(cnt);

    // console.log(contours.size());
    // for(let i=1; i<contours.size(); i++) {
    //     let cnt1 = contours.get(i);
    //     let rect1 = cv.boundingRect(cnt1);
    //     rect = rect | rect1;
    // }

    image = image.roi(rect);

    let height = image.rows;
    let width = image.cols;

    if (height > width) {
        height = IMAGE_SIZE;
        const scaleFactor = image.rows / height;
        width = Math.round(image.cols / scaleFactor);
    } else {
        width = IMAGE_SIZE;
        const scaleFactor = image.cols / width;
        height = Math.round(image.rows / scaleFactor);
    }

    let dSize = new cv.Size(width, height);
    cv.resize(image, image, dSize, 0, 0, cv.INTER_AREA);

    const LEFT = Math.ceil(PADDING + (IMAGE_SIZE - width) / 2);
    const RIGHT = Math.floor(PADDING + (IMAGE_SIZE - width) / 2);
    const TOP = Math.ceil(PADDING + (IMAGE_SIZE - height) / 2);
    const BOTTOM = Math.floor(PADDING + (IMAGE_SIZE - height) / 2);

    const BLACK = new cv.Scalar(0, 0, 0, 0);
    cv.copyMakeBorder(image, image, TOP, BOTTOM, LEFT, RIGHT, cv.BORDER_CONSTANT, BLACK);

    cv.findContours(image, contours, hierarchy, cv.RETR_CCOMP, cv.CHAIN_APPROX_SIMPLE);
    cnt = contours.get(0);
    const moments = cv.moments(cnt, false);
    const cx = moments.m10 / moments.m00;
    const cy = moments.m01 / moments.m00;

    const X_SHIFT = Math.round(image.cols / 2 - cx);
    const Y_SHIFT = Math.round(image.rows / 2 - cy);

    let dsize = new cv.Size(image.rows, image.cols);
    let shiftMatrix = cv.matFromArray(2, 3, cv.CV_64FC1, [1, 0, X_SHIFT, 0, 1, Y_SHIFT]);
    cv.warpAffine(image, image, shiftMatrix, dsize, cv.INTER_LINEAR, cv.BORDER_CONSTANT, new cv.Scalar());

    let pixelValues = image.data;
    pixelValues = Float32Array.from(pixelValues);

    pixelValues = pixelValues.map(function (item) {
        return item / 255;
    });

    const X = tf.tensor([pixelValues]);
    const result = model.predict(X);
    result.print();

    const output = result.dataSync()[0];
    //Testing only, delete later
    // let outputCanvas = document.createElement('CANVAS');
    // cv.imshow(outputCanvas, image);
    // document.body.appendChild(outputCanvas);

    // Clean up
    image.delete();
    contours.delete();
    cnt.delete();
    hierarchy.delete();
    shiftMatrix.delete();
    X.dispose();
    result.dispose();

    return output;
}